{
["autorun"] = true,
["name"] = "CustomIndicGlow",
["code"] = "-- Load the LibCustomGlow-1.0 library, which provides glow effects for UI elements\nlocal LCG = LibStub(\"LibCustomGlow-1.0\")\n\n-- Define options for different types of glow effects (Pixel, Normal, Shine, and Proc)\nlocal GLOW_OPTS = {\n    PIXEL = {\n        LINES = 6, -- Number of lines in the pixel glow\n        FREQUENCY = 0.25, -- Frequency of the glow pulse\n        LENGTH = 10, -- Length of each glow line\n        THICKNESS = 1, -- Thickness of the glow lines\n    },\n    NORMAL = {\n        FREQUENCY = 0.125   -- Frequency of the normal glow pulse\n    },\n    SHINE = {\n        PARTICLE_GROUPS = 9, -- Number of particle groups in the shine glow\n        FREQUENCY = 0.5, -- Frequency of the shine glow pulse\n        SCALE = 1.5, -- Scale of the shine glow\n        X_OFFSET = 1, -- X-axis offset for the shine glow\n        Y_OFFSET = 1          -- Y-axis offset for the shine glow\n    },\n    PROC = {\n        X_OFFSET = 0, -- X-axis offset for the proc glow\n        Y_OFFSET = 0, -- Y-axis offset for the proc glow\n        DURATION = 0, -- Duration of the proc glow\n        START_ANIMATION = true -- Whether to start the animation immediately\n    }\n}\n\n-- Define the target indicators and their associated glow settings\nlocal targetIndicators = {\n    [\"Defensive Cooldowns\"] = {\n        glowType = \"PIXEL\", -- Type of glow to use\n        color = { 0, 1, 0, 1 }, -- RGBA color for the glow (green in this case)\n        opts = GLOW_OPTS.PIXEL     -- Options for the pixel glow, as defined above\n    },\n    [\"External Cooldowns\"] = {\n        glowType = \"PIXEL\", -- Type of glow to use\n        color = { 0, 1, 0, 1 }, -- RGBA color for the glow (green in this case)\n        opts = GLOW_OPTS.PIXEL     -- Options for the pixel glow, as defined above\n    },\n  --  [\"Healers\"] = {\n --       glowType = \"PIXEL\", -- Type of glow to use\n --       color = { 0, 1, 0, 1 }, -- RGBA color for the glow (green in this case)\n  --      opts = GLOW_OPTS.PIXEL     -- Options for the pixel glow, as defined above\n  --  },\n    [\"Riptide (ReatUI)\"] = {\n        glowType = \"PIXEL\", -- Type of glow to use\n        color = { 0, 1, 0, 1 }, -- RGBA color for the glow (green in this case)\n        opts = GLOW_OPTS.PIXEL     -- Options for the pixel glow, as defined above\n    },\n}\n\nlocal function ShowGlow(frame, glowType, color, opts)\n    if glowType == \"NORMAL\" then\n        LCG.PixelGlow_Stop(frame)\n        LCG.AutoCastGlow_Stop(frame)\n        LCG.ProcGlow_Stop(frame)\n        LCG.ButtonGlow_Start(frame, color)\n    elseif glowType == \"PIXEL\" then\n        LCG.ButtonGlow_Stop(frame)\n        LCG.AutoCastGlow_Stop(frame)\n        LCG.ProcGlow_Stop(frame)\n        -- color, N, frequency, length, thickness\n        LCG.PixelGlow_Start(frame, color, opts.LINES, opts.FREQUENCY, opts.LENGTH, opts.THICKNESS)\n    elseif glowType == \"SHINE\" then\n        LCG.ButtonGlow_Stop(frame)\n        LCG.PixelGlow_Stop(frame)\n        LCG.ProcGlow_Stop(frame)\n        -- color, N, frequency, scale\n        LCG.AutoCastGlow_Start(frame, color, opts.PARTICLE_GROUPS, opts.FREQUENCY, opts.SCALE, opts.X_OFFSET, opts.Y_OFFSET)\n    else\n        LCG.ButtonGlow_Stop(frame)\n        LCG.PixelGlow_Stop(frame)\n        LCG.AutoCastGlow_Stop(frame)\n        LCG.ProcGlow_Stop(frame)\n    end\nend\n\nhooksecurefunc(Cell.iFuncs, \"CreateAura_BarIcon\", function(name)\n        local f = _G[name]\n        if Cell.vars.currentLayoutTable and Cell.vars.currentLayoutTable.indicators then\n            for _, v in pairs(Cell.vars.currentLayoutTable.indicators) do\n                if targetIndicators[v[\"name\"]] then\n                    local expectedSuffix = v[\"indicatorName\"]\n                    if string.find(name, expectedSuffix) then\n                        local glowSettings = targetIndicators[v[\"name\"]]\n                        local originalSetCooldown = f.SetCooldown\n                        f.SetCooldown = function(frame, ...)\n                            originalSetCooldown(frame, ...)\n                            ShowGlow(\n                                frame,\n                                glowSettings.glowType,\n                                glowSettings.color,\n                                glowSettings.opts\n                            )\n                        end\n                    end\n                end\n            end\n        end\nend)",
},
