["preferToUpdate"] = false,
["customText"] = "function()\n    if not aura_env.state or WeakAuras.IsOptionsOpen() then return end \n    -- set variables\n    local state = aura_env.state\n    local config = aura_env.config\n    local text = aura_env.state.name\n    if not text then \n        text = aura_env.state.name\n    end\n    local IDoptions = aura_env.IDoptions\n    local options = aura_env.options\n    local submsg = strtrim(text:gsub(\"~\", \"\")) -- cut ~\n    local cutmsg = strtrim(text:gsub(\"^~ ?\", \"\"):gsub(\"%s+%(%d+%)\",\"\"))  -- cut number and ~\n    local nbmsg = strtrim(text:gsub(\"%s+%(%d+%)\",\"\")) -- cut number\n    \n    \n    -- adjust text according to options if needed\n    if options and options[text] and options[text].replaceSpellName and options[text].spellName  then\n        text = options[text].spellName\n        --spell Name\n    elseif state.spellId and IDoptions and IDoptions[state.spellId] and IDoptions[state.spellId].replaceSpellName and IDoptions[state.spellId].spellName then\n        text = IDoptions[state.spellId].spellName\n        --spell ID\n    elseif options and options[submsg] and options[submsg].replaceSpellName and options[submsg].spellName then\n        text = options[submsg].spellName\n        --spell Name ignore ~\n    elseif options and options[cutmsg] and options[cutmsg].replaceSpellName and options[cutmsg].spellName and options[cutmsg].IgnoreAlertNumber then\n        text = options[cutmsg].spellName\n        --spell Name ignore Spellcount\n        \n    elseif config.NameOption == 2 then\n        text = submsg\n        -- hide ~ \n    elseif config.NameOption == 3 then\n        text = nbmsg\n        -- hide number\n    elseif config.NameOption == 4 then\n        text = cutmsg\n        -- hide number and ~\n    end\n    \n    \n    -- more Text Adjustments\n    if not text then return end \n    local IconAdjust = string.len(string.match(text,\"|T.-|t\") or \"\") -- find length of any icon within the name\n    text = strtrim(text) -- Trim   \n    if IconAdjust > 0 then IconAdjust = IconAdjust - 2 end -- Make icons take 2 characters in calculation\n    if (config.textMaxLength or 0)>=5 and text:len()-IconAdjust>config.textMaxLength then -- check if length exceeeds the maximum \n        text = WA_Utf8Sub(text,config.textMaxLength+IconAdjust)..\"..\" -- Truncate     \n    end\n    -- adjust texts for paused options\n    if state.paused and config.textPausedOptions == 1 then   \n        text = \"Paused\"\n    elseif state.paused and config.textPausedOptions == 2 then\n        text = text..\"Paused\"\n    elseif state.paused and config.textPausedOptions == 3 then\n        text = WA_Utf8Sub(text,text:len()-8)..\"..\"\n        text = text..\"Paused\"\n    end \n    \n    -- Adjust color according to custom option (this should be a condition but not possible yet)\n    if IDoptions and IDoptions[state.spellId] and IDoptions[state.spellId].replaceTextColor then\n        local r,g,b,a = unpack(IDoptions[state.spellId].textColor)\n        text = string.format(\"\\124c%02x%02x%02x%02x%s\\124r\",255*a,255*r,255*g,255*b,text)\n        \n        \n    elseif options and options[state.message] and options[state.message].replaceTextColor then\n        local r,g,b,a = unpack(options[state.message].textColor)\n        text = string.format(\"\\124c%02x%02x%02x%02x%s\\124r\",255*a,255*r,255*g,255*b,text)\n        \n    elseif options and options[submsg] and options[submsg].replaceTextColor and aura_env.options[submsg].IgnoreAlertNumber then\n        local r,g,b,a = unpack(options[submsg].textColor)\n        text = string.format(\"\\124c%02x%02x%02x%02x%s\\124r\",255*a,255*r,255*g,255*b,text)\n        \n    elseif options and options[cutmsg] and options[cutmsg].replaceTextColor and options[cutmsg].IgnoreAlertNumber then\n        local r,g,b,a = unpack(options[cutmsg].textColor)\n        text = string.format(\"\\124c%02x%02x%02x%02x%s\\124r\",255*a,255*r,255*g,255*b,text)\n        \n        \n    elseif state.dbmColor and aura_env.config.UseBossModsColor then\n        local r,g,b = unpack(state.dbmColor)\n        text = string.format(\"\\124c%02x%02x%02x%02x%s\\124r\",255,255*r,255*g,255*b,text)\n    elseif state.bwTextColor and aura_env.config.UseBossModsColor then\n        local r,g,b = unpack(state.bwTextColor)\n        text = string.format(\"\\124c%02x%02x%02x%02x%s\\124r\",255,255*r,255*g,255*b,text)\n        \n    end\n    return text\nend\n\n\n",
