["custom"] = "function(allstates)\n    \n    local cfg = aura_env.config.tracking\n    local lang = GetLocale()\n    \n    -- is this thing even on?\n    if not cfg.trackingEnable then return end\n    \n    for i, db in pairs(cfg.tracked) do\n        if db.enable and db.id ~= 0 then\n            \n            local count, iconID, name, percent\n            if db.type == 1 then\n                count   = (GetItemCount(db.id, true, true, true) or 0)\n                iconID  = GetItemInfo(db.id) and select(10, GetItemInfo(db.id))\n                name    = GetItemInfo(db.id)\n                percent = \"\"\n            else\n                local c = C_CurrencyInfo.GetCurrencyInfo(db.id)\n                count   = c.quantity or 0\n                iconID  = c.iconFileID\n                name    = C_CurrencyInfo.GetCurrencyInfo(db.id).name\n                percent = (cfg.showCap and c.maxQuantity > 0) and \" (\" .. math.floor(c.quantity / c.maxQuantity * 100) .. \"%)\" or \"\"\n                \n            end\n            \n            -- show icon?\n            local icon = (cfg.iconEnable and iconID) and \" |T\" .. iconID .. \":0|t\" or \"\"\n            \n            -- build the bar\n            if (cfg.hideMissing and count > 0) or not cfg.hideMissing then\n                allstates[db.id] = {\n                    autoHide = true,\n                    changed  = true,\n                    index    = i,\n                    info     = aura_env.addCommas(count) .. percent .. icon,\n                    name     = name,\n                    show     = true,\n                    \n                    -- colors\n                    r = db.r / 255,\n                    g = db.g / 255,\n                    b = db.b / 255,\n                }\n            end\n        end\n    end\n    \n    return true   \nend",
