["custom"] = "function(allstates, event, ...)\n    \n    -- config and db\n    local cfg = aura_env.config.mplus\n    local db = aura_env.db\n    local LRS = _G.LibStub.libs['LibOpenRaid-1.0']\n    \n    -- is details installed?\n    if not _G.LibStub or not LRS then return end\n    \n    -- populate our keystone database\n    local keyDB = LRS.GetAllKeystonesInfo()\n    \n    -- is this thing even on?\n    if not cfg.mplusEnable or not keyDB then return end\n    \n    -- rcv key info from chat_msg_addon\n    if (event == \"CHAT_MSG_ADDON\" and select(1,...) == \"LRS\") then\n        \n        -- check each units key\n        for unit, x in pairs(keyDB) do\n            local type = \"\"\n            if unit == UnitName(\"player\") then\n                type = \"player\"\n            elseif UnitInRaid(unit) and cfg.raid then\n                type = \"raid\"\n            elseif UnitInParty(unit) and not UnitInRaid(unit) and cfg.party then\n                type = \"party\"\n            elseif aura_env.GuildRoster[unit .. \"-\" .. GetNormalizedRealmName()] and cfg.guild then\n                type = \"guild\"\n            end\n            \n            -- should we show this? build the bar\n            if type ~= \"\" and unit and x and x.level > 0 then\n                local keyID = x.challengeMapID\n                local keystone = db[keyID] and (db[keyID][GetLocale()] or db[keyID][\"enUS\"]) or \"???\"\n                allstates[unit] = {\n                    changed = true,\n                    color = RAID_CLASS_COLORS[select(2, GetClassInfo(x.classID))],\n                    event = \"key_\" .. unit,\n                    index = unit,\n                    info = keystone .. \"+\" .. x.level,\n                    name = string.match(unit, \"^(.-)%-\") or unit,\n                    show = true,\n                    type = type\n                }\n            end\n        end\n        \n    elseif event == \"GROUP_ROSTER_UPDATE\" or event == \"GROUP_LEFT\" or event == \"CANCEL_SUMMON\" or event == \"START\" then\n        \n        -- should we as for new information?\n        C_ChatInfo.SendAddonMessage(\"LRS\", \"update\", \"WHISPER\", UnitName(\"player\"))\n        if cfg.party and UnitInParty(\"player\") then LRS.RequestKeystoneDataFromParty() end\n        if cfg.raid and UnitInRaid(\"player\") then LRS.RequestKeystoneDataFromRaid() end\n        if cfg.guild and IsInGuild() then LRS.RequestKeystoneDataFromGuild() end\n        \n        -- clean out stale keys\n        for unit, x in pairs(allstates) do\n            if (not UnitInParty(unit) and x.type == \"party\") then allstates[unit] = { changed = true, show = false } end\n            if (not UnitInRaid(unit)  and x.type == \"raid\") then allstates[unit] = { changed = true, show = false } end\n            if (not aura_env.GuildRoster[unit] and x.type == \"guild\") then allstates[unit] = { changed = true, show = false } end\n        end\n    end\n    \n    return true\nend\n\n\n\n\n\n",
