["triggers"] = {
{
["trigger"] = {
["type"] = "custom",
["subeventSuffix"] = "_CAST_START",
["unit"] = "player",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["custom_type"] = "stateupdate",
["useName"] = false,
["events"] = "UNIT_POWER_UPDATE\nRUNE_POWER_UPDATE\nPLAYER_TARGET_CHANGED\nUNIT_SPELLCAST_SUCCEEDED \nUNIT_AURA",
["names"] = {
},
["check"] = "event",
["custom"] = "function(allstates, event, ...)\n    -- 技能信息表\n    local spells = {\n        [55233] = {cost = {runicPower = 0, runes = 0}, boneShield = 0}, -- 吸血鬼之血\n        [49998] = {cost = {runicPower = 35, runes = 0}},   -- 灵界打击\n        [195182] = {cost = {runicPower = -20, runes = 2}, boneShield = 3}, -- 骨髓分裂\n        [206930] = {cost = {runicPower = -15, runes = 1}}, -- 心脏打击\n        [195292] = {cost = {runicPower = -10, runes = 1}, boneShield = 2}, -- 死神的抚摸\n        [194679] = {cost = {runicPower = -10, runes = 1}}, -- 符文分流\n        [43265] = {cost = {runicPower = -10, runes = 1}},  -- 枯萎凋零\n        [49028] = {cost = {runicPower = 0, runes = 0}, boneShield = 5},    -- 符文刃舞\n        [194844] = {cost = {runicPower = 0, runes = 0}, boneShield = -5},  -- 白骨风暴\n        [219809] = {cost = {runicPower = 0, runes = 0}, boneShield = -5},    -- 墓石\n        [50842] = {cost = {runicPower = 0, runes = 0}},   -- 血液沸腾\n        [439843] = {cost = {runicPower = -20, runes = 2}},   -- 死神印记\n        [274156] = {cost = {runicPower = 0, runes = 0}, boneShield = 0},    -- 吞噬\n    }\n\n    --技能缓存\n    local spellBookCache = {}\n    local function cachedFindSpellBookSlotBySpellID(spellID)\n        if spellBookCache[spellID] == nil then\n            spellBookCache[spellID] = FindSpellBookSlotBySpellID(spellID)\n        end\n        return spellBookCache[spellID]\n    end\n\n    -- 获取玩家状态\n    local function getPlayerState()\n        local state = {\n            health = UnitHealth(\"player\") / UnitHealthMax(\"player\") * 100,\n            runicPower = UnitPower(\"player\", Enum.PowerType.RunicPower),\n            runes = 0,\n            buffs = {},\n            cooldowns = {}\n        }\n        for i = 1, 6 do\n            if select(3, GetRuneCooldown(i)) then\n                state.runes = state.runes + 1\n            end\n        end\n        return state\n    end\n\n    -- 检查buff和CD\n    local function checkBuffsAndCDs(state)\n        local function checkAura(spellId, unit, auraType)\n            local name, _, count, _, duration, expirationTime, source, _, _, id = AuraUtil.FindAuraByName(C_Spell.GetSpellInfo(spellId).name, unit, auraType)\n            if id == spellId and (unit == \"player\" or source == \"player\") then\n                return {active = true, count = count or 0, remaining = expirationTime and (expirationTime - GetTime()) or 0}\n            end\n            return {active = false, count = 0, remaining = 0}\n        end\n\n        local buffIds = {\n            195181, -- 白骨之盾\n            194679, -- 符文分流\n            81141,   -- 赤色天灾\n            188290, -- 枯萎凋零\n            441416, -- 破灭1\n            447954, -- 破灭2\n            81256, -- 符文刃舞\n        }\n        local debuffIds = {\n\n        }\n        \n        for _, id in ipairs(buffIds) do\n            state.buffs[id] = checkAura(id, \"player\", \"HELPFUL\")\n        end\n        \n        for _, id in ipairs(debuffIds) do\n            state.debuffs[id] = checkAura(id, \"target\", \"HARMFUL\")\n        end\n\n        -- 检查技能CD\n        for spellId, spellInfo in pairs(spells) do\n            local spellCD = C_Spell.GetSpellCooldown(spellId)\n            state.cooldowns[spellId] = (spellCD.startTime + spellCD.duration) - GetTime()\n        end\n    end\n\n    -- 获取技能冷却信息\n    local function getSpellCooldown(spellId)\n        local spellCD = C_Spell.GetSpellCooldown(spellId)\n        local spellCharges = C_Spell.GetSpellCharges(spellId)\n        if spellCharges then\n            local rechargeTime = (spellCharges.currentCharges < spellCharges.maxCharges) and (spellCharges.cooldownStartTime + spellCharges.cooldownDuration - GetTime()) or 0\n            return spellCharges.currentCharges, rechargeTime, spellCharges.maxCharges\n        elseif spellCD then\n            local remainingCD = (spellCD.startTime and spellCD.duration) and math.max(spellCD.startTime + spellCD.duration - GetTime(), 0) or 0\n            return 0, remainingCD, 0\n        else\n            return 0, 0, 0\n        end\n    end\n\n    -- 优先级判断函数\n    local function getPriority(state, excludeSpell)\n        local function canUseSpell(id)\n            if not cachedFindSpellBookSlotBySpellID(id) then\n                return false\n            end\n            if (state.cooldowns[id] and state.cooldowns[id] > 1.5) then\n                return false\n            end\n            if spells[id].cost.runicPower > 0 and state.runicPower < spells[id].cost.runicPower then\n                return false\n            end\n            if spells[id].cost.runes > 0 and state.runes < spells[id].cost.runes then\n                return false\n            end\n            return true\n        end\n\n        local priorityList = {\n            {id = 55233, condition = function() \n                return state.health <= 50 and excludeSpell ~= 55233 end},  -- 吸血鬼之血\n            {id = 194679, condition = function() \n                return state.health <= 80 and not state.buffs[194679].active and excludeSpell ~= 194679 end},  -- 符文分流\n            {id = 49998, condition = function() \n                return state.health <= 50 and state.runicPower >= 35 end},  -- 灵界打击\n            {id = 49028, condition = function() \n                return not state.buffs[81256].active and excludeSpell ~= 49028 end},  -- 符文刃舞\n            {id = 194844, condition = function() \n                return state.buffs[195181].count >= 5 and state.cooldowns[49028] > 10 and excludeSpell ~= 194844 end},  -- 白骨风暴\n            {id = 219809, condition = function() \n                return (state.health <= 60 or state.cooldowns[49028] > 10) and state.buffs[195181].count > 5 and excludeSpell ~= 219809 end},  -- 墓石\n            {id = 195292, condition = function() \n                return state.buffs[195181].remaining < 8 end},  -- 死神的抚摩\n            {id = 43265, condition = function() \n                return (state.buffs[81141].active or not state.buffs[188290].active) and excludeSpell ~= 43265 end},  -- 枯萎凋零\n            {id = 439843, condition = function() \n                return excludeSpell ~= 439843 end},  -- 死神印记\n            {id = 50842, condition = function() \n                return select(1, getSpellCooldown(50842)) >= 2 and excludeSpell ~= 50842 end},  -- 血液沸腾\n            {id = 274156, condition = function() \n                return excludeSpell ~= 274156 end},  -- 吞噬\n            {id = 195182, condition = function() \n                return (state.buffs[441416].active or state.buffs[447954].active) and excludeSpell ~= 195182 end},  -- 精髓分裂\n            {id = 195292, condition = function() \n                return state.buffs[195181].count < 9 end},  -- 死神的抚摩\n            {id = 195182, condition = function() \n                return state.buffs[195181].count < 8 end},  -- 精髓分裂\n            {id = 49998, condition = function() \n                return state.runicPower > 90 end},  -- 灵界打击\n            {id = 206930, condition = function() \n                return state.buffs[195181].count >= 8 and state.runes >= 1 end},  -- 心脏打击\n            {id = 49998, condition = function() \n                return state.runicPower >= 35 and select(1, getSpellCooldown(50842)) == 0 end},  -- 灵界打击\n            {id = 50842, condition = function() \n                return excludeSpell ~= 50842 end},  -- 血液沸腾\n            {id = 195292, condition = function() \n                return true end},  -- 死神的抚摩\n            {id = 206930, condition = function() \n                return true end},  -- 心脏打击\n        }\n\n        for _, spell in ipairs(priorityList) do\n            if canUseSpell(spell.id) and spell.condition() then\n                return spell.id\n            end\n        end\n\n        return 0\n    end\n\n    -- 主逻辑\n    local state = getPlayerState()\n    checkBuffsAndCDs(state)\n    local firstPriority = getPriority(state)\n    -- print(firstPriority)\n    local firstCharges, firstCD, firstMaxCharges = getSpellCooldown(firstPriority)\n\n    -- 更新状态\n    state.runicPower = math.max(state.runicPower - (spells[firstPriority] and spells[firstPriority].cost.runicPower or 0), 0)\n    state.runes = math.max(state.runes - (spells[firstPriority] and spells[firstPriority].cost.runes or 0), 0)\n\n    -- 更新骨盾层数\n    local oldBoneShieldCount = state.buffs[195181].count\n    if spells[firstPriority] and spells[firstPriority].boneShield then\n        state.buffs[195181].count = math.max(math.min(state.buffs[195181].count + spells[firstPriority].boneShield, 12), 0)\n        -- 只在骨盾增加时更新持续时间\n        if state.buffs[195181].count > oldBoneShieldCount then\n            state.buffs[195181].remaining = 30\n        end\n    end\n\n    if firstPriority == 43265 then  -- 枯萎凋零\n        state.buffs[188290].active = true\n    elseif firstPriority == 50842 then  -- 血液沸腾\n        local charges, _, _ = getSpellCooldown(50842)\n        state.bloodBoilCharges = charges - 1\n    elseif firstPriority == 49028 then  -- 符文刃舞\n        state.cooldowns[49028] = 60  -- 设置60秒CD\n    elseif firstPriority == 195292 then  -- 死神的抚摩\n        state.cooldowns[195292] = 6\n    elseif firstPriority == 50842 then  -- 血沸\n        state.cooldowns[50842] = 6.6\n    end\n\n    local secondPriority = getPriority(state, firstPriority)\n    local secondCharges, secondCD, secondMaxCharges = getSpellCooldown(secondPriority)\n\n    \n    -- 辅助函数：安全地获取法术图标\n    local function getSafeSpellIcon(spellId)\n        if not spellId or spellId == 0 then\n            return 0  \n        end\n        local spellInfo = C_Spell.GetSpellInfo(spellId)\n        return spellInfo and spellInfo.iconID or 0\n    end\n    \n    -- 更新 allstates\n    allstates[1] = {\n        show = true,\n        changed = true,\n        icon = getSafeSpellIcon(firstPriority),\n        spell = firstPriority,\n        cooldown = firstCD,\n        charges = firstCharges,\n        maxCharges = firstMaxCharges\n    }\n\n    allstates[2] = {\n        show = secondPriority ~= 0,\n        changed = true,\n        icon = getSafeSpellIcon(secondPriority), \n        spell = secondPriority,\n        cooldown = secondCD,\n        charges = secondCharges,\n        maxCharges = secondMaxCharges\n    }\n\n    return true\nend",
