["custom"] = "function(states, event, ...)\n    if event == \"PLAYER_ENTERING_WORLD\" or event == \"STATUS\" or event == \"MKPT_REFRESH\" or event == \"OPTIONS\" then\n        if not aura_env.professions or next(aura_env.professions) == nil then\n            aura_env.professions = aura_env.GetProfessionInfo()\n        end\n        \n        local index = 0\n        for professionId, professionInfo in pairs(aura_env.professions) do\n            index = index + 1\n            \n            -- Profession Cell\n            local remaining = aura_env.GetProfesionRemainingKp(professionId)\n            states[professionId]={\n                changed = true,\n                show = true,\n                name = professionInfo.professionName..\" \"..tostring(professionInfo.skillLevel)..\"/\"..tostring(professionInfo.maxSkillLevel),\n                icon = professionInfo.icon,\n                index = index,\n                glow = false,\n                type=0,\n                rightText= \"U: \"..tostring(remaining.unique),\n                leftText=\"W: \"..tostring(remaining.weekly),\n            }\n            \n            -- Item cells\n            for _, itemInfo in pairs(aura_env.GetItemInfoTableByProfessionID(professionId)) do\n                index = index + 1\n                \n                local cell = aura_env.FillItemCell(states[itemInfo.item], itemInfo, professionInfo.expanded)\n                cell.index = index\n                \n                states[itemInfo.item] = cell\n            end\n        end\n        \n        -- Update description if a cell is selected\n        local highlightedItemInfo = aura_env.FindItemInfoByItemId(aura_env.highlightedItemId)\n        local highlightedItemDescription = aura_env.ItemInfoDescription(highlightedItemInfo)\n        WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", highlightedItemDescription)\n        \n    elseif event == \"ENCOUNTER_LOOT_RECEIVED\" then\n        -- Refreshs the list if loot received is belongs to the profession\n        local _, itemId, _ = ...\n        local itemInfo = aura_env.FindItemInfoByItemId(itemId)\n        if not itemInfo then\n            return false\n        end\n        \n        local itemCell = states[itemId]\n        local show = itemCell.show\n        \n        itemCell = aura_env.FillItemCell(itemCell, itemInfo, aura_env.professions[itemInfo.professionId].expanded)\n        itemCell.show = itemCell.show and show\n        \n        local professionCell = states[itemInfo.professionId]\n        if professionCell then\n            local remaining = aura_env.GetProfesionRemainingKp(itemInfo.professionId)\n            professionCell.rightText= \"U: \"..tostring(remaining.unique)\n            professionCell.leftText=\"W: \"..tostring(remaining.weekly)\n            professionCell.changed = true\n        end\n        \n        if aura_env.highlightedItemId == itemId then\n            aura_env.highlightedItemId = nil\n            C_Map.ClearUserWaypoint()\n            C_SuperTrack.ClearAllSuperTracked()\n        end\n        \n        return true\n        \n    elseif event == \"MKPT_CELL_CLICKED\" then\n        local cellId, _ = ...\n        local itemInfo = aura_env.FindItemInfoByItemId(cellId)\n        \n        -- Collapse/Expand if it's a quest cell\n        if not itemInfo then\n            local professionId = cellId\n            aura_env.professions[professionId].expanded = not aura_env.professions[professionId].expanded\n            WeakAuras.ScanEvents(\"MKPT_REFRESH\")\n            return false\n        end\n        \n        if aura_env.highlightedItemId == cellId then\n            aura_env.highlightedItemId = nil\n            C_Map.ClearUserWaypoint()\n        else\n            -- Set Waypoint/TomTom Arrow\n            aura_env.highlightedItemId = cellId           \n            aura_env.TrackItem(itemInfo)          \n        end\n        \n        C_Timer.After(0.5, function () WeakAuras.ScanEvents(\"MKPT_REFRESH\") end )\n        return false\n        \n    elseif event ==\"SKILL_LINE_SPECS_RANKS_CHANGED\" or event== \"SKILL_LINE_SPECS_UNLOCKED\" then\n        if not aura_env.professions then return false end\n        local professions = aura_env.GetProfessionInfo()\n        for k, v in pairs(professions) do\n            local professionCell = states[k]\n            if  professionCell then                \n                professionCell.changed = true\n                professionCell.show = true\n                professionCell.name = v.professionName..\" \"..tostring(v.skillLevel)..\"/\"..tostring(v.maxSkillLevel)\n            end\n        end\n        \n        return true\n    elseif event == \"LEARNED_SPELL_IN_SKILL_LINE\" then\n        local spellId, professionSlotId, _ = ...\n        if professionSlotId and aura_env.IsSpellKhazAlgarMainProfession(spellId) then\n            aura_env.professions = nil\n            for _,v in pairs(states) do\n                v.changed = true\n                v.show = false\n            end\n            \n            C_Timer.After(1, function () WeakAuras.ScanEvents(\"MKPT_REFRESH\") end )\n        end\n    end\n    \n    return true\n    \nend",
