["init"] = {
["custom"] = "-----------------------------------------------------------------\n------------- Nonstandard Lua Functions by Blizzard -------------\n-----------------------------------------------------------------\n\nlocal strsplittable = strsplittable;\nlocal strtrim = strtrim;\nlocal tinsert = tinsert;\n\n--BlizzardInterfaceCode/Interface/SharedXML/TableUtil.lua\nlocal CopyTable = CopyTable;\n\n-----------------------------------------------------------------\n----------------------------- Util ------------------------------\n-----------------------------------------------------------------\n\nlocal Util = {};\n\nlocal MSG_TEMPLATE_ILLEGAL_TYPE = \"Illegal %s (%s expected, got %s)\";\n\nfunction Util.Check(expression, errorMessageTemplate, ...)\n    if(not expression) then\n        error(errorMessageTemplate and string.format(errorMessageTemplate, ...) or \"check failed\");\n    end\nend\n\nfunction Util.CheckType(argName, argValue, expectedType)\n    local actualType = type(argValue);\n    Util.Check(actualType == expectedType, MSG_TEMPLATE_ILLEGAL_TYPE, argName, expectedType, actualType);\n    \n    return argValue;\nend\n\nfunction Util.StrToNumArray(delimiter, str)\n    local tbl = {};\n    \n    for _, value in ipairs(strsplittable(delimiter, str)) do\n        tinsert(tbl, tonumber(strtrim(value)));\n    end\n    \n    return tbl;\nend\n\n-----------------------------------------------------------------\n------------------------- LabelResolver -------------------------\n-----------------------------------------------------------------\n\nlocal LABEL_PATTERN            = \"{(%l+)=(%d+)/?([^{}]*)}\";\nlocal QUEST_LABEL_PATTERN    = \"{quest=(%d+)/?([^{}]*)}\";\nlocal ITEM_LABEL_PATTERN    = \"{item=(%d+)/?([^{}]*)}\";\n\nlocal LABEL_FORMAT            = \"{%s=%d/%s}\";\nlocal NPC_LINK_FORMAT        = \"unit:Creature-0-0-0-0-%d\";\n\nlocal LabelResolver            = {};\nlocal LabelPrepared            = {};\nlocal LabelCache             = {};\n\nlocal function GetLeftTextOfTooltip(tooltipData)\n    local line1args = tooltipData and tooltipData.lines and tooltipData.lines[1] and tooltipData.lines[1].args;\n    \n    if(line1args) then\n        for _, line1arg in ipairs(line1args) do\n            if(line1arg and line1arg.field == \"leftText\") then\n                return line1arg.stringVal;\n            end\n        end\n    end\nend\n\nfunction LabelResolver.Prepare(label)\n    if(not label) then\n        return;\n    end\n    \n    for key, value in string.gmatch(label, LABEL_PATTERN) do\n        local id = tonumber(value);\n        local cacheIndex = string.format(LABEL_FORMAT, key, id, \"\");\n        \n        if(not LabelPrepared[cacheIndex]) then\n            if(key == \"quest\") then\n                C_QuestLog.RequestLoadQuestByID(id);\n            elseif(key == \"item\") then\n                C_Item.RequestLoadItemDataByID(id);\n            elseif(key == \"spell\") then\n                C_Spell.RequestLoadSpellData(id);\n            elseif(key == \"npc\") then\n                --C_TooltipInfo.GetHyperlink(string.format(NPC_LINK_FORMAT, id)); --preload\n            end\n            \n            LabelPrepared[cacheIndex] = true;\n        end\n    end\nend\n\nfunction LabelResolver.Resolve(label)\n    if(not label) then\n        return;\n    end\n    \n    local icons, iconIndex = {}, 0;\n    local textResolved = string.gsub(label, LABEL_PATTERN, function(key, value, default)\n            iconIndex = iconIndex + 1;\n            \n            local id = tonumber(value);\n            local cacheIndex = string.format(LABEL_FORMAT, key, id, \"\");\n            local queried = LabelCache[cacheIndex];\n            \n            if(queried) then\n                tinsert(icons, iconIndex, queried.icon);\n                return queried.text;\n            end\n            \n            if(key == \"quest\") then\n                local name = C_QuestLog.GetTitleForQuestID(id);\n                queried = name and {text = name, cacheable = true};\n            elseif(key == \"item\") then\n                local name, _, _, _, _, _, _, _, _, icon = GetItemInfo(id);\n                local cacheable = name and icon and true;\n                queried = name and {text = name, icon = icon, cacheable = cacheable};\n            elseif(key == \"spell\") then\n                local name, _, icon = GetSpellInfo(id);\n                local cacheable = name and icon and true;\n                queried = name and {text = name, icon = icon, cacheable = cacheable};\n            elseif(key == \"npc\") then\n                local tooltipData = C_TooltipInfo.GetHyperlink(string.format(NPC_LINK_FORMAT, id));\n                local name = GetLeftTextOfTooltip(tooltipData);\n                queried = name and {text = name, cacheable = true};\n            elseif(key == \"skill\") then\n                local name = C_TradeSkillUI.GetTradeSkillDisplayName(id); --see also C_TradeSkillUI.GetProfessionInfoBySkillLineID\n                queried = name and {text = name, cacheable = true};\n            elseif(key == \"map\") then\n                local mapInfo = C_Map.GetMapInfo(id);\n                local name = mapInfo and mapInfo.name;\n                queried = name and {text = name, cacheable = true};\n            elseif(key == \"zone\") then\n                local name = C_Map.GetAreaInfo(id);\n                queried = name and {text = name, cacheable = true};\n            end\n            \n            if(queried) then\n                if(queried.cacheable) then\n                    LabelCache[cacheIndex] = queried;\n                end\n                \n                tinsert(icons, iconIndex, queried.icon);\n                return queried.text;\n            else\n                return string.len(default) > 0 and default or cacheIndex;\n            end\n    end);\n    \n    return textResolved, icons, iconIndex;\nend\n\n-----------------------------------------------------------------\n------------------------- ProgressMixin -------------------------\n-----------------------------------------------------------------\n\nlocal ProgressCount = 0;\nlocal ProgressMixin = {};\n\nlocal function ContainsTooltipAttr(attrs)\n    if(not attrs) then\n        return false;\n    elseif(attrs.spellId or attrs.itemId or attrs.link or attrs.tooltip) then\n        return true;\n    elseif(attrs.unit and (attrs.unitBuffIndex or attrs.unitDebuffIndex or attrs.unitAuraIndex )) then\n        return true;\n    else\n        return false;\n    end\nend\n\nfunction ProgressMixin:Init(attrs)\n    attrs = (attrs == nil) and {} or Util.CheckType(\"attrs\", attrs, \"table\");\n    \n    ProgressCount = ProgressCount + 1;\n    self.id    = ProgressCount;\n    self.attrs = attrs or {};\nend\n\nfunction ProgressMixin:GetId()\n    return self.id;\nend\n\n--fill name, note, tooltip, icon, progressType, value, total... in sub Mixin;\n--the returned state wouldn't show if its progressType is not \"static\" or \"timed\";\nfunction ProgressMixin:GetState()\n    return CopyTable(self.attrs, true);\nend\n\n-----------------------------------------------------------------\n------------------- LabelSupportProgressMixin -------------------\n-----------------------------------------------------------------\n\nlocal LabelSupportProgressMixin    = CreateFromMixins(ProgressMixin);\n\nfunction LabelSupportProgressMixin:Init(category, name, note, attrs)\n    if(category) then Util.CheckType(\"category\", category, \"string\"); end\n    if(name) then Util.CheckType(\"name\", name, \"string\"); end\n    if(note) then Util.CheckType(\"note\", note, \"string\"); end\n    \n    attrs = (attrs == nil) and {} or Util.CheckType(\"attrs\", attrs, \"table\");\n    attrs.name = category and string.format(\"[%s] %s\", category, name or \"\") or name or \"\";\n    attrs.note = note;\n    if(attrs.tooltip) then Util.CheckType(\"attrs.tooltip\", attrs.tooltip, \"string\"); end\n    if(attrs.iconLabel) then Util.CheckType(\"attrs.iconLabel\", attrs.iconLabel, \"string\"); end    \n    \n    ProgressMixin.Init(self, attrs);\n    LabelResolver.Prepare(self.attrs.name);\n    LabelResolver.Prepare(self.attrs.note);\n    LabelResolver.Prepare(self.attrs.tooltip);\n    LabelResolver.Prepare(self.attrs.iconLabel);\nend\n\n--fill progressType, value, total... in sub Mixin;\n--the returned state wouldn't show if its progressType is not \"static\" or \"timed\";\nfunction LabelSupportProgressMixin:GetState()\n    local state = ProgressMixin.GetState(self);\n    state.name = LabelResolver.Resolve(state.name);\n    state.note = LabelResolver.Resolve(state.note);\n    state.tooltip = LabelResolver.Resolve(state.tooltip);\n    \n    if(state.iconLabel) then\n        local _, icons, iconCount = LabelResolver.Resolve(state.iconLabel);\n        \n        for iconIndex = 1, iconCount do\n            if(icons[iconIndex]) then\n                state.icon = icons[iconIndex];\n                break;\n            end\n        end\n    end\n    \n    return state;\nend\n\n-----------------------------------------------------------------\n-------------------------- QuestsMixin --------------------------\n-----------------------------------------------------------------\n\nlocal function CheckQuests(quests)\n    Util.CheckType(\"quests\", quests, \"table\");\n    \n    for index, quest in pairs(quests) do\n        Util.CheckType(\"quest at index \" .. tostring(index), quest, \"number\");\n    end\n    \n    return quests;\nend\n\n---------------------- FlaggedQuestsMixin -----------------------\n\nlocal FlaggedQuestsMixin = CreateFromMixins(LabelSupportProgressMixin);\n\nfunction FlaggedQuestsMixin:Init(category, name, note, quests, attrs)\n    attrs = (attrs == nil) and {} or Util.CheckType(\"attrs\", attrs, \"table\");\n    attrs.total = attrs.total and min(Util.CheckType(\"attrs.total\", attrs.total, \"number\"), #quests) or #quests;\n    \n    LabelSupportProgressMixin.Init(self, category, name, note, attrs);\n    self.quests = CheckQuests(quests);\nend\n\nfunction FlaggedQuestsMixin:GetState()\n    local completed = 0;\n    \n    for _, quest in pairs(self.quests) do\n        if(C_QuestLog.IsQuestFlaggedCompleted(quest)) then\n            completed = completed + 1;\n        end\n    end\n    \n    local state = LabelSupportProgressMixin.GetState(self);\n    state.progressType = \"static\";\n    state.value = completed;\n    state.total = state.total;\n    \n    return state;\nend\n\nlocal function FlaggedQuests(category, name, note, quests, attrs)\n    return CreateAndInitFromMixin(FlaggedQuestsMixin, category, name, note, quests, attrs);\nend\n\nlocal function FlaggedQuest(category, name, note, quest, attrs)\n    if(not quest) then\n        quest = name and string.match(name, QUEST_LABEL_PATTERN);\n        quest = quest and tonumber(quest);\n    end\n    \n    return FlaggedQuests(category, name, note, {quest}, attrs);\nend\n\nlocal function Rare(category, name, note, quest, attrs)\n    return FlaggedQuests(category, name, note, {quest}, attrs);\nend\n\nlocal function ItemReward(category, name, note, quests, attrs)\n    local itemReward = FlaggedQuests(category, name, note, quests, attrs);\n    local itemId = name and string.match(name, ITEM_LABEL_PATTERN);\n    itemId = itemId and tonumber(itemId);\n    \n    if(itemId and not itemReward.attrs.icon) then\n        itemReward.attrs.iconLabel = string.format(LABEL_FORMAT, \"item\", itemId, \"\");\n    end\n    \n    if(itemId and not ContainsTooltipAttr(itemReward.attrs)) then\n        itemReward.attrs.itemId = itemId;\n    end\n    \n    return itemReward;\nend\n\n------------------- BonusObjectiveQuestsMixin -------------------\n\nlocal BonusObjectiveQuestsMixin = CreateFromMixins(LabelSupportProgressMixin);\n\nfunction BonusObjectiveQuestsMixin:Init(category, name, note, quests, attrs)\n    LabelSupportProgressMixin.Init(self, category, name, note, attrs);\n    self.quests = CheckQuests(quests);\nend\n\nfunction BonusObjectiveQuestsMixin:GetState()\n    local completed, actived = 0, 0;\n    \n    for _, quest in pairs(self.quests) do\n        if(C_QuestLog.IsQuestFlaggedCompleted(quest)) then\n            completed = completed + 1;\n        elseif(C_TaskQuest.GetQuestTimeLeftSeconds(quest)) then\n            actived = actived + 1;\n        end\n    end\n    \n    local total = completed + actived;\n    \n    if(total > 0) then\n        local state = LabelSupportProgressMixin.GetState(self);\n        state.progressType = \"static\";\n        state.value = completed;\n        state.total = total;\n        \n        return state;\n    end\nend\n\nlocal function BonusObjectiveQuests(category, name, note, quests, attrs)\n    return CreateAndInitFromMixin(BonusObjectiveQuestsMixin, category, name, note, quests, attrs);\nend\n\nlocal function BonusObjectiveQuest(category, name, note, quest, attrs)\n    if(not quest) then\n        quest = name and string.match(name, QUEST_LABEL_PATTERN);\n        quest = quest and tonumber(quest);\n    end\n    \n    return BonusObjectiveQuests(category, name, note, {quest}, attrs);\nend\n\nlocal function WorldQuest(category, name, note, quest, attrs)\n    if(not quest) then\n        quest = name and string.match(name, QUEST_LABEL_PATTERN);\n        quest = quest and tonumber(quest);\n    end\n    \n    return BonusObjectiveQuests(category, name, note, {quest}, attrs);\nend\n\n-----------------------------------------------------------------\n---------------- BonusObjectiveQuestTimberMixin -----------------\n-----------------------------------------------------------------\n\nlocal BonusObjectiveQuestTimberMixin = CreateFromMixins(LabelSupportProgressMixin);\n\nfunction BonusObjectiveQuestTimberMixin:Init(category, name, note, quest, attrs)\n    attrs = (attrs == nil) and {} or Util.CheckType(\"attrs\", attrs, \"table\");\n    attrs.duration = attrs.duration and Util.CheckType(\"attrs.duration\", attrs.duration, \"number\") or 5;\n    \n    LabelSupportProgressMixin.Init(self, category, name, note, attrs);\n    self.quest = Util.CheckType(\"quest\", quest, \"number\");\nend\n\nfunction BonusObjectiveQuestTimberMixin:GetState()\n    local secLeft = C_TaskQuest.GetQuestTimeLeftSeconds(self.quest);\n    \n    if(secLeft) then\n        local state = LabelSupportProgressMixin.GetState(self);\n        state.progressType = \"timed\";\n        state.expirationTime = GetTime() + secLeft;\n        state.duration = state.duration;\n        \n        return state;\n    end\nend\n\nlocal function BonusObjectiveQuestTimber(category, name, note, quest, attrs)\n    return CreateAndInitFromMixin(BonusObjectiveQuestTimberMixin, category, name, note, quest, attrs);\nend\n\n-----------------------------------------------------------------\n------------------------ ProgressCommon -------------------------\n-----------------------------------------------------------------\n\naura_env.ProgressCommon = {\n    Util                 = Util,\n    \n    LABEL_FORMAT        = LABEL_FORMAT,\n    \n    FlaggedQuests        = FlaggedQuests,\n    FlaggedQuest        = FlaggedQuest,\n    Rare                = Rare,\n    ItemReward            = ItemReward,\n    \n    BonusObjectiveQuests= BonusObjectiveQuests,\n    BonusObjectiveQuest    = BonusObjectiveQuest,\n    WorldQuest            = WorldQuest,\n    \n    BonusObjectiveQuestTimber = BonusObjectiveQuestTimber,\n};",
