["custom"] = "function(allstates, event, ...)\n    local UnitPower, UnitExists, GetTime = UnitPower, UnitExists, GetTime\n    \n    -- 技能信息表\n    local spells = {\n        [49143] = {name = \"冰霜打击\", cost = {runicPower = 30, runes = 0}},\n        [49184] = {name = \"凛风冲击\", cost = {runicPower = -10, runes = 1}},\n        [49020] = {name = \"湮灭\", cost = {runicPower = -20, runes = 2}},\n        [196770] = {name = \"冷酷寒冬\", cost = {runicPower = 0, runes = 0}},\n        [43265] = {name = \"枯萎凋零\", cost = {runicPower = -10, runes = 1}},\n        [47568] = {name = \"强化符文武器\", cost = {runicPower = 0, runes = 0}},\n        [51271] = {name = \"冰霜之柱\", cost = {runicPower = 0, runes = 0}},\n        [279302] = {name = \"冰霜疫病\", cost = {runicPower = 0, runes = 0}},\n        [343294] = {name = \"灵魂收割\", cost = {runicPower = -10, runes = 1}},\n        [439843] = {name = \"死神印记\", cost = {runicPower = -20, runes = 2}},\n    }\n\n    -- 技能缓存\n    local spellBookCache = {}\n    local function cachedFindSpellBookSlotBySpellID(spellID)\n        if spellBookCache[spellID] == nil then\n            spellBookCache[spellID] = FindSpellBookSlotBySpellID(spellID)\n        end\n        return spellBookCache[spellID]\n    end\n\n    -- 获取玩家状态\n    local function getPlayerState()\n        local state = {\n            runicPower = UnitPower(\"player\", Enum.PowerType.RunicPower),\n            runes = 0,\n            maxRunes = 6,  \n            targetExists = UnitExists(\"target\"),\n            inCombat = UnitAffectingCombat(\"player\"),\n            buffs = {},\n            debuffs = {},\n            cooldowns = {},\n            targetCount = 0,\n        }\n    \n        for i = 1, 6 do\n            if select(3, GetRuneCooldown(i)) then\n                state.runes = state.runes + 1\n            end\n        end\n    \n        if state.targetExists then\n            local targetCurrentHealth = UnitHealth(\"target\")\n            local targetMaxHealth = UnitHealthMax(\"target\")\n            state.targetHealth = targetCurrentHealth\n            state.targetHealthPercentage = targetMaxHealth > 0 and (targetCurrentHealth / targetMaxHealth) * 100 or 0\n        else\n            state.targetHealth = 0\n            state.targetHealthPercentage = 0\n        end\n    \n        return state\n    end\n\n    -- 检查buff和CD\n    local function checkBuffsAndCDs(state)\n        local function checkAura(spellId, unit, auraType)\n            local name, _, count, _, duration, expirationTime, source, _, _, id = AuraUtil.FindAuraByName(C_Spell.GetSpellInfo(spellId).name, unit, auraType)\n            if id == spellId and (unit == \"player\" or source == \"player\") then\n                return {active = true, count = count or 0, remaining = expirationTime and (expirationTime - GetTime()) or 0}\n            end\n            return {active = false, count = 0, remaining = 0}\n        end\n        \n        local buffIds = {\n            51124,  -- 杀戮机器\n            59052,  -- 冰霜之镰\n            188290, -- 枯萎凋零\n            194879, -- 冰冷之爪\n            51271, --冰霜之柱\n        }\n        local debuffIds = {\n            55095,  -- 冰霜疫病\n            51714,  -- 锋锐之霜\n        }\n        \n        for _, id in ipairs(buffIds) do\n            state.buffs[id] = checkAura(id, \"player\", \"HELPFUL\")\n        end\n        \n        for _, id in ipairs(debuffIds) do\n            state.debuffs[id] = checkAura(id, \"target\", \"HARMFUL\")\n        end\n        \n        -- 检查技能CD\n        for spellId, spellInfo in pairs(spells) do\n            local spellCD = C_Spell.GetSpellCooldown(spellId)\n            state.cooldowns[spellId] = (spellCD.startTime + spellCD.duration) - GetTime()\n        end\n        \n        -- 统计受到冰霜疫病影响的目标数量\n        local function getAoeTargetCount()\n            local aoeTargets = 0\n            local maxTargetsToCheck = 3  -- 我们只需要知道是否有3个或更多目标\n        \n            for i = 1, 40 do\n                local unit = \"nameplate\" .. i\n                if UnitExists(unit) and UnitCanAttack(\"player\", unit) then\n                    local name, _, _, _, _, expirationTime, source, _, _, spellId = AuraUtil.FindAuraByName(C_Spell.GetSpellInfo(55095).name, unit, \"HARMFUL\")\n                    if spellId == 55095 and source == \"player\" then\n                        aoeTargets = aoeTargets + 1\n                        if aoeTargets >= maxTargetsToCheck then\n                            return maxTargetsToCheck  -- 如果已经找到3个目标，直接返回\n                        end\n                    end\n                end\n            end\n        \n            return aoeTargets\n        end\n        state.aoeTargets = getAoeTargetCount()\n    end\n\n    -- 获取技能冷却信息\n    local function getSpellCooldown(spellId)\n        local spellCD = C_Spell.GetSpellCooldown(spellId)\n        local spellCharges = C_Spell.GetSpellCharges(spellId)\n        if spellCharges then\n            local rechargeTime = (spellCharges.currentCharges < spellCharges.maxCharges) and (spellCharges.cooldownStartTime + spellCharges.cooldownDuration - GetTime()) or 0\n            return spellCharges.currentCharges, rechargeTime, spellCharges.maxCharges\n        elseif spellCD then\n            local remainingCD = (spellCD.startTime and spellCD.duration) and math.max(spellCD.startTime + spellCD.duration - GetTime(), 0) or 0\n            return 0, remainingCD, 0\n        else\n            return 0, 0, 0\n        end\n    end\n\n    -- 优先级判断函数\n    local function getPriority(state, excludeSpell)\n        local function canUseSpell(id)\n            if not cachedFindSpellBookSlotBySpellID(id) then\n                return false\n            end\n            if (state.cooldowns[id] and state.cooldowns[id] > 1.5) then\n                return false\n            end\n            if spells[id].cost.runicPower > 0 and state.runicPower < spells[id].cost.runicPower then\n                return false\n            end\n            if spells[id].cost.runes > 0 and state.runes < spells[id].cost.runes then\n                return false\n            end\n            return true\n        end\n        \n        local priorityList = {\n            -- {id = 47568, condition = function() -- 强化符文武器\n            --     return canUseSpell(47568) and excludeSpell ~= 47568\n            -- end},\n            {id = 49020, condition = function() -- 湮灭(有杀戮机器）\n                return state.buffs[51271].active \n                    and state.buffs[51124].count > 0\n            end},\n            {id = 49143, condition = function() -- 冰霜打击（冰冷之爪快过期）\n                return state.aoeTargets < 3 and state.buffs[194879].remaining < 3 and excludeSpell ~= 49143\n            end},\n            {id = 196770, condition = function() -- 冷酷寒冬\n                return canUseSpell(196770) and excludeSpell ~= 196770\n            end},\n            {id = 49184, condition = function() -- 凛风冲击（无冰霜疫病）\n                return not state.debuffs[55095].active and excludeSpell ~= 49184\n            end},\n            {id = 43265, condition = function() -- 枯萎凋零\n                return state.aoeTargets > 1 and not state.buffs[188290].active and excludeSpell ~= 43265\n            end},\n            {id = 439843, condition = function() -- 死神印记 冰霜之柱\n                return canUseSpell(51271) and excludeSpell ~= 439843\n            end},\n            {id = 343294, condition = function() -- 灵魂收割\n                return state.aoeTargets < 3 and state.targetExists and state.targetHealthPercentage < 35 and excludeSpell ~= 343294\n            end},\n            {id = 49020, condition = function() -- 湮灭(有杀戮机器）\n                return state.buffs[51124].count > 0\n            end},\n            {id = 49143, condition = function() -- 冰霜打击（锋锐之霜满层）\n                return state.debuffs[51714].count >= 5 and excludeSpell ~= 49143\n            end},\n            {id = 49184, condition = function() -- 凛风冲击（有白霜）\n                return state.buffs[59052].active and excludeSpell ~= 49184\n            end},\n            {id = 49020, condition = function() -- 溢出湮灭\n                return state.runes > 4\n            end},\n            {id = 49143, condition = function() -- 冰霜打击\n                return true\n            end},\n            {id = 49020, condition = function() -- 湮灭\n                return true\n            end},\n        }\n\n        for _, spell in ipairs(priorityList) do\n            if canUseSpell(spell.id) and spell.condition() then\n                return spell.id\n            end\n        end\n\n        return 0\n    end\n\n    -- 主逻辑\n    local state = getPlayerState()\n    checkBuffsAndCDs(state)\n    local firstPriority = getPriority(state)\n    local firstCharges, firstCD, firstMaxCharges = getSpellCooldown(firstPriority)\n\n    -- 更新状态\n    if spells[firstPriority] then\n        local spellInfo = spells[firstPriority]\n        state.runicPower = math.max(state.runicPower - spellInfo.cost.runicPower, 0)\n        state.runes = math.max(state.runes - spellInfo.cost.runes, 0)\n\n        if firstPriority == 49184 then  -- 凛风冲击\n            state.debuffs[55095].active = true\n            state.debuffs[55095].remaining = 24  -- 假设持续时间为24秒\n            if state.buffs[59052].active then\n                state.runes = state.runes + 1\n            end\n        elseif firstPriority == 49020 then  -- 湮灭\n            state.buffs[51124].count = math.max(state.buffs[51124].count - 1, 0)\n        elseif firstPriority == 49143 and state.debuffs[51714].count >= 5 then  -- 冰霜打击\n            state.debuffs[51714].count = 0\n        elseif firstPriority == 43265 then  -- 枯萎凋零\n            state.buffs[188290].active = true\n            state.buffs[188290].remaining = 10  -- 假设持续时间为10秒\n        end\n\n        -- 减少所有 BUFF 和 DOT 的持续时间 1 秒\n        for _, buff in pairs(state.buffs) do\n            buff.remaining = math.max(buff.remaining - 1, 0)\n        end\n        for _, debuff in pairs(state.debuffs) do\n            debuff.remaining = math.max(debuff.remaining - 1, 0)\n            debuff.active = debuff.remaining > 0\n        end\n    end\n\n    local secondPriority = getPriority(state, firstPriority)\n    local secondCharges, secondCD, secondMaxCharges = getSpellCooldown(secondPriority)\n\n    -- 辅助函数：安全地获取法术图标\n    local function getSafeSpellIcon(spellId)\n        if not spellId or spellId == 0 then\n            return 0  \n        end\n        local spellInfo = C_Spell.GetSpellInfo(spellId)\n        return spellInfo and spellInfo.iconID or 0\n    end\n\n    -- 更新 allstates\n    allstates[1] = {\n        show = true,\n        changed = true,\n        icon = getSafeSpellIcon(firstPriority),\n        spell = firstPriority,\n        cooldown = firstCD,\n        charges = firstCharges,\n        maxCharges = firstMaxCharges\n    }\n\n    allstates[2] = {\n        show = secondPriority ~= 0,\n        changed = true,\n        icon = getSafeSpellIcon(secondPriority), \n        spell = secondPriority,\n        cooldown = secondCD,\n        charges = secondCharges,\n        maxCharges = secondMaxCharges\n    }\n\n    -- if allstates and allstates[1] and allstates[1].spell then\n    --     local defaultSpellId = 6603  -- 自动攻击的法术ID\n    --     local spellName = C_Spell.GetSpellInfo(allstates[1].spell).name\n    --     if spellName then\n    --         C_ChatInfo.SendAddonMessage(\"MyKeyBind\", spellName, \"WHISPER\", UnitName(\"player\"))\n    --     end\n    -- end\n\n    return true\nend",
