["custom"] = "function(allstates)\n    local db = aura_env.db\n    local cfg = aura_env.config.vault\n    \n    -- is this thing even on?\n    if not cfg.vaultEnable then return end\n    \n    -- is the fruit ripe for picking?\n    if C_WeeklyRewards.CanClaimRewards() then\n        allstates[\"vault_claim\"] = {\n            event        = \"vault_claim\",\n            changed      = true,\n            name         = \"開啟您的寶庫！\",\n            stacks       = db.icons.chest,\n            progressType = \"static\",\n            show         = true,\n            total        = 100,\n            value        = 0,\n        }\n        \n    else\n        for event, x in pairs(db.events) do\n            if cfg[event] then\n                \n                -- grab vault progress\n                local response = C_WeeklyRewards.GetActivities(x.code)\n                if not response or (response and not response[1]) then return end\n                \n                -- build the bar\n                allstates[event] = {\n                    event        = event,\n                    changed      = true,\n                    name         = x.name[GetLocale()] or x.name[\"enUS\"],\n                    stacks       = aura_env.GetVaultQuality(response[1].level, response[1].activityTierID, event) .. \" \" .. aura_env.GetVaultQuality(response[2].level, response[2].activityTierID, event) .. \" \" .. aura_env.GetVaultQuality(response[3].level, response[3].activityTierID, event),\n                    progressType = \"static\",\n                    show         = true,\n                    total        = 100,\n                    value        = 0,\n                }\n            end\n        end\n    end\n    \n    return true\nend",
