["init"] = {
["custom"] = "aura_env.db = {\n    \n    [\"icons\"] = {\n        [\"rank1\"] = \"|A:Professions-ChatIcon-Quality-Tier1:20:20|a\",\n        [\"rank2\"] = \"|A:Professions-ChatIcon-Quality-Tier2:20:20|a\",\n        [\"rank3\"] =\"|A:Professions-ChatIcon-Quality-Tier3:20:20|a\",\n        [\"rank4\"] =\"|A:Professions-ChatIcon-Quality-Tier4:20:20|a\",\n        [\"rank5\"] =\"|A:Professions-ChatIcon-Quality-Tier5:20:20|a\",\n        [\"chest\"] = \"|A:worldquest-questmarker-questionmark:20:20|a\",\n        [\"none\"] = \"|A:xmarksthespot:20:20|a\",\n    },\n    \n    [\"events\"] = {\n        [\"mplus\"] = {\n            [\"name\"] = {\n                [\"enUS\"] = \"Mythic+\",\n                [\"zhCN\"] = \"大秘境\",\n                [\"zhTW\"] = \"傳奇+\",\n            },\n            [\"code\"] = 1\n        },\n        [\"delves\"] = {\n            [\"name\"] = {\n                [\"enUS\"] = \"Delves\",\n                [\"zhCN\"] = \"地下堡\",\n                [\"zhTW\"] = \"探究\",\n            },\n            [\"code\"] = 6\n        },\n        [\"raid\"] = {\n            [\"name\"] = {\n                [\"enUS\"] = \"Raid\",\n                [\"zhCN\"] = \"团队副本\",\n                [\"zhTW\"] = \"團隊副本\",\n            },\n            [\"code\"] = 3\n        }\n    }\n}\n\nfunction aura_env.GetVaultQuality(value, activityID, content)\n    if content == \"raid\" then\n        if aura_env.config.vault.icons then\n            return value == 16 and aura_env.db.icons.rank5  or (value == 15 and aura_env.db.icons.rank3 or (value == 14 and aura_env.db.icons.rank2 or (value == 17 and aura_env.db.icons.rank1 or aura_env.db.icons.none)))\n        else\n            return value == 16 and \"|cFFff8000   M|r\" or (value == 15 and \"|cFFa335ee   H|r\" or (value == 14 and \"|cFF1979ff   N|r\" or (value == 17 and \"|cFF1eff00   L|r\" or \"|cFFc41e3a   X|r\")))\n        end\n        \n    elseif content == \"delves\" then\n        if aura_env.config.vault.icons then\n            return value == 11 and aura_env.db.icons.rank5  or (value >= 8 and aura_env.db.icons.rank3 or (value >= 4 and aura_env.db.icons.rank2 or (value > 0 and aura_env.db.icons.rank1 or aura_env.db.icons.none)))\n        else\n            return value == 11 and \"  |cFFff8000 11|r\" or (value >= 8 and \"    |cFFa335ee\" .. value .. \"|r\" or (value >= 4 and \"    |cFF1979ff\" .. value .. \"|r\" or (value > 0 and \"    |cFF1eff00\" .. value .. \"|r\" or \"   |cFFc41e3aX|r\")))\n        end\n        \n    else\n        if aura_env.config.vault.icons then\n            return value >= 10 and aura_env.db.icons.rank5 or (value >= 6 and aura_env.db.icons.rank4 or (value >= 3 and aura_env.db.icons.rank3 or (value >= 2 and aura_env.db.icons.rank2 or (activityID ~= 0 and aura_env.db.icons.rank1 or aura_env.db.icons.none))))\n        else\n            return value >= 10 and \"|cFFff8000 +\" .. value .. \"|r\" or (value >= 6 and \"|cFFa335ee +\" .. value .. \"|r\" or (value >= 3 and \"|cFF0070dd +\" .. value .. \"|r\" or (value >= 2 and \"|cFF1eff00 +\" .. value .. \"|r\" or (activityID == 1 and \"|cFFffffffTW|r\" or (activityID == 2 and \"|cFFffffffH|r\" or (activityID == 3 and \"|cFFffffff0|r\" or \"|cFFc41e3a X|r\"))))))\n        end\n    end\nend",
