["custom"] = "function(allstates, evt, ...)\n    \n    if evt == \"GLOBAL_MOUSE_UP\" and ... then\n        local button = ...\n        if button == \"LeftButton\" and IsShiftKeyDown() then\n            for cloneID, region in pairs(aura_env.regions) do\n                local over = MouseIsOver(region.icon)\n                if over and allstates[cloneID] and allstates[cloneID].event == \"treatise\" then\n                    for _, link in ipairs(allstates[cloneID].links) do\n                        C_TradeSkillUI.SetRecipeTracked(link,true,false)\n                    end\n                end\n            end\n        end\n        \n    else\n        -- build vars\n        local cfg = aura_env.config.profSettings\n        local db  = aura_env.db\n        \n        -- is this thing even on?\n        if not cfg.profEnable then return end\n        \n        -- start checking weekly events\n        for event, v in pairs(db.event) do\n            local lang = GetLocale()\n            local name = v.name[lang] or v.name[\"enUS\"]\n            local completed, required = 0, 0\n            local info = \"\"\n            local tooltip = name .. \"|n\"\n            local links = {}\n            \n            -- should we care about this event?\n            if cfg[event] then\n                local sort = v.sort\n                for profID, x in pairs(v.questDB) do\n                    \n                    -- do we even have this profession / are we ignoring it?\n                    local prof1, prof2 = GetProfessions()\n                    prof1 = prof1 and select(7, GetProfessionInfo(prof1))\n                    prof2 = prof2 and select(7, GetProfessionInfo(prof2))\n                    if (profID == prof1 and not cfg.profIgnore.ignore[db.prof[profID].idx]) or (profID == prof2 and not cfg.profIgnore.ignore[db.prof[profID].idx]) then\n                        completed = 0\n                        required = x.questReq\n                        local profName = db.prof[profID].name[lang] or db.prof[profID].name[\"enUS\"]\n                        \n                        -- grab profession skill points\n                        local configID = C_ProfSpecs.GetConfigIDForSkillLine(db.prof[profID].skillID)\n                        local profTree = configID and C_Traits.GetConfigInfo(configID) and C_Traits.GetConfigInfo(configID).treeIDs\n                        local skillC, skillT = 0, 0\n                        if profTree then\n                            for _, tree in pairs(profTree) do\n                                for _, node in pairs(C_Traits.GetTreeNodes(tree)) do\n                                    local nodeDB = C_Traits.GetNodeInfo(configID, node)\n                                    skillT = skillT + nodeDB.maxRanks - 1\n                                    skillC = nodeDB.ranksPurchased > 1 and skillC + nodeDB.currentRank - 1 or skillC\n                                end\n                            end\n                        end\n                        \n                        -- are we ignoring this because it's capped?\n                        if cfg.hideMax and skillC == skillT then else\n                            \n                            -- calculate completion\n                            local questComplete = false\n                            for _, questID in pairs(x.questID) do\n                                questComplete = C_QuestLog.IsQuestFlaggedCompleted(questID)\n                                completed = questComplete and completed + 1 or completed\n                                \n                                -- add tooltip info for treatise & drops\n                                if event == \"treatise\" and not questComplete then\n                                    if v.extra[questID] and GetItemInfo(v.extra[questID].itemID) then\n                                        tooltip = tooltip .. \"|n\" .. select(2,GetItemInfo(v.extra[questID].itemID))\n                                        table.insert(links,v.extra[questID].recipeID)\n                                    end\n                                elseif event == \"drops\" and not questComplete then\n                                    local enemy = v.extra[questID].enemy[lang] or v.extra[questID].enemy[\"enUS\"]\n                                    if v.extra[questID] and GetItemInfo(v.extra[questID].itemID) then\n                                        tooltip = tooltip .. \"|n\" .. select(2,GetItemInfo(v.extra[questID].itemID)) .. \" \" .. enemy\n                                    end\n                                end\n                            end\n                            \n                            -- build info and color completion as required\n                            profName    = completed >= required and \"|cFFaad372\" .. profName .. \"|r\" or profName\n                            local tinfo = v.showCount and profName .. \" \" .. (completed >= required and \"|cFFaad372\" .. completed .. \"/\" .. required .. \"|r\" or completed .. \"/\" .. required) or profName\n                            \n                            info = info == \"\" and tinfo or info .. \" | \" .. tinfo\n                        end\n                    end\n                end\n                \n                -- add tooltip footer\n                if v.extra then\n                    tooltip = tooltip .. v.extra.foot\n                end\n                \n                -- build the bar (or destroy irrelevant ones)\n                if not cfg.hideCompleted or (completed < required and not allstates[event]) then\n                    allstates[event] = {\n                        autoHide     = true,\n                        changed      = true,\n                        event        = event,\n                        icon         = \"vignetteloot\",\n                        index        = sort,\n                        info         = info,\n                        links        = links,\n                        name         = name,\n                        progressType = \"static\",\n                        show         = true,\n                        tooltip      = tooltip,\n                        total        = 100,\n                        value        = 0,\n                    }\n                    \n                elseif allstates[event] then allstates[event] = { changed = true, show = false, event = event } end\n            end  \n        end\n        \n        return true   \n    end\nend",
