["actions"] = {
["start"] = {
},
["init"] = {
["custom"] = "local a = aura_env\n--\n-- Original author: Chilly @ Draenor\n-- Modified by: Qweekk/Whattheqweek @ Kazzak\n--\n\nif not _G[\"AutoTankMisdirect\"] then\n    local locale = GetLocale()\n    local _, class, _ = UnitClass(\"player\")\n    a.btn = CreateFrame(\"Button\", \"AutoTankMisdirect\", UIParent, \"SecureActionButtonTemplate\")\n    a.btn:SetAttribute(\"type\", \"spell\")\n    a.btn:SetAttribute(\"unit\", \"player\")\n    if class == \"HUNTER\" then\n        a.btn:SetAttribute(\"spell\", 34477)\n    else\n        -- sm大地盾\n        a.btn:SetAttribute(\"spell\", 974)\n    end\n    a.btn:SetAttribute(\"checkselfcast\", false)\n    a.btn:SetAttribute(\"checkfocuscast\", false)\nelse\n    a.btn = _G[\"AutoTankMisdirect\"]\nend\n\n\nfunction aura_env.UpdateTank()\n    if aura_env.config.focus and UnitExists(\"focus\") and UnitIsFriend(\"player\", \"focus\") then\n        aura_env.btn:SetAttribute(\"unit\", \"focus\")\n        return\n    end\n    \n    for unit in WA_IterateGroupMembers() do\n        if UnitGroupRolesAssigned(unit) == \"TANK\" then\n            aura_env.btn:SetAttribute(\"unit\", unit)\n            return\n        end\n    end\n    -- If focus is not being used and no tank exists in party, fall back to md on pet\n    if UnitExists(\"pet\") then\n        aura_env.btn:SetAttribute(\"unit\", \"pet\")\n    else\n        aura_env.btn:SetAttribute(\"unit\", \"mouseover\")\n    end\nend\n\n\n",
["do_custom"] = true,
},
