["unit"] = "player",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["custom"] = "--\n-- Author: Chilly @ Draenor\n--\nfunction(e, arg1)\n    if e == \"GROUP_ROSTER_UPDATE\" or e == \"CHALLENGE_MODE_START\" or e == \"PLAYER_ENTERING_WORLD\" or \"PLAYER_FOCUS_CHANGED\" or (e == \"UNIT_PET\" and arg1 == \"player\") then\n        if InCombatLockdown() then\n            aura_env.needUpdate = true\n        else\n            aura_env.UpdateTank()\n        end\n    elseif aura_env.needUpdate then\n        aura_env.UpdateTank()\n        aura_env.needUpdate = false\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
