["init"] = {
["custom"] = "aura_env.is_shortage = false\naura_env.display_text = \"\"\n\nlocal ROLE_NAMES = {\n    [1] = \"坦克\",\n    [2] = \"治疗\",\n    [3] = \"DPS\"\n}\nlocal PLAYER_ROLES = { UnitGetAvailableRoles(\"player\") }\n\nlocal function load_dungeons()\n    local available_dungeons = {}\n    if aura_env.config.should_track_dungeons == true then\n        for i = 1, GetNumRandomDungeons() do\n            local dungeon_id = GetLFGRandomDungeonInfo(i)\n            available_dungeons[#available_dungeons + 1] = dungeon_id\n        end\n    end\n    if aura_env.config.should_track_raids == true then\n        for i = 1, GetNumRFDungeons() do\n            local dungeon_id = GetRFDungeonInfo(i)\n            available_dungeons[#available_dungeons + 1] = dungeon_id\n        end\n    end\n    return available_dungeons\nend\n\nlocal function check_dungeon_shortage(dungeon_id)\n    local applicable_roles = {}\n    for shortage_type = 1, LFG_ROLE_NUM_SHORTAGE_TYPES do\n        local eligible, for_tank, for_healer, for_damage, item_count, money, xp = GetLFGRoleShortageRewards(dungeon_id,\n        shortage_type)\n        \n        if not eligible or item_count == 0 then\n            break\n        end\n        local for_roles = { [1] = for_tank, [2] = for_healer, [3] = for_damage }\n        for i, for_role in ipairs(for_roles) do\n            if for_role and PLAYER_ROLES[i] then\n                table.insert(applicable_roles, ROLE_NAMES[i])\n            end\n        end\n        if #applicable_roles > 0 then\n            return applicable_roles\n        end\n    end\n    \n    return nil\nend\n\naura_env.routine = function()\n    aura_env.is_shortage = false\n    aura_env.display_text = \"\"\n    RequestLFDPlayerLockInfo()\n    local available_dungeons = load_dungeons()\n    for _, dungeon_id in ipairs(available_dungeons) do\n        local applicable_roles = check_dungeon_shortage(dungeon_id)\n        if applicable_roles then\n            local dungeon_name, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, _, lfg_map_id = GetLFGDungeonInfo(dungeon_id)\n            if lfg_map_id then\n                dungeon_name = GetRealZoneText(lfg_map_id) .. \": \" .. dungeon_name\n            end\n            aura_env.is_shortage = true\n            aura_env.display_text = aura_env.display_text ..\n            dungeon_name .. \" (\" .. table.concat(applicable_roles, \", \") .. \") 符文包裹奖励\\n\"\n        end\n    end\n    \nend\n\naura_env.event_emitter = function()\n    WeakAuras.ScanEvents(\"LFG_BONUS_TRIGGER\", nil)\n    C_Timer.After(10, aura_env.event_emitter)\nend\n\nif _G[\"LFG_BONUS_FIRST_RUN\"] == nil then\n    C_Timer.After(10, aura_env.event_emitter)\n    _G[\"LFG_BONUS_FIRST_RUN\"] = true\nend\n\n\n",
