["events"] = "START, CHAT_MSG_LOOT, UNIT_INVENTORY_CHANGED",
["custom"] = "function(allstates)\n    \n    local cfg = aura_env.config.factions\n    \n    -- is this thing even on?\n    if not cfg.factionEnable then return end\n    \n    -- check which factions we care about\n    for key, value in pairs(cfg) do\n        \n        -- only care about factions (ignore other settings)\n        if tonumber(key) and value then\n            key = tonumber(key)\n            \n            -- grab information\n            local repType = C_MajorFactions.GetMajorFactionData(key) and \"RENOWN\" or \"FRIEND\"\n            local data = repType == \"RENOWN\" and C_MajorFactions.GetMajorFactionData(key) or C_GossipInfo.GetFriendshipReputation(key)\n            local name = string.match(data.name, \"^(.-)%-\") and string.match(data.name, \"^(.-)%-\") or data.name\n            \n            -- crunch numbers\n            local current = repType == \"RENOWN\" and data.renownReputationEarned or data.standing - data.reactionThreshold\n            local max = repType == \"RENOWN\" and data.renownLevelThreshold or data.nextThreshold - data.reactionThreshold\n            local progress = cfg.percent and math.floor(current / max * 100) .. \"%\" or current .. \"/\" .. max\n            \n            -- build the bar\n            allstates[key] = {\n                show = true,\n                changed = true,\n                progressType = \"static\",\n                name = name,\n                index = cfg.sort == 2 and repType == \"RENOWN\" and 1 .. name or name,\n                info = repType == \"RENOWN\" and data.renownLevel .. \" (\" .. progress .. \")\" or data.reaction .. \" (\" .. progress .. \")\",\n                value = current,\n                total = max,\n                event = key\n            }\n        end\n    end\n    \n    return true\nend",
