["event"] = "Health",
["unit"] = "player",
["names"] = {
},
["events"] = "UNIT_POWER_UPDATE\nRUNE_POWER_UPDATE\nPLAYER_TARGET_CHANGED\nUNIT_SPELLCAST_SUCCEEDED \nUNIT_AURA",
["spellIds"] = {
},
["subeventPrefix"] = "SPELL",
["check"] = "event",
["custom_type"] = "stateupdate",
["custom"] = "function(allstates, event, ...)\n    local UnitPower, UnitExists, GetTime = UnitPower, UnitExists, GetTime\n    \n    -- 技能信息表\n    local spells = {\n        [46584] = {name = \"亡者复生\", cost = {runicPower = 0, runes = 0}},\n        [63560] = {name = \"黑暗突变\", cost = {runicPower = 0, runes = 0}},\n        [77575] = {name = \"爆发\", cost = {runicPower = -10, runes = 1}},\n        [85948] = {name = \"脓疮打击\", cost = {runicPower = -20, runes = 2}},\n        [55090] = {name = \"天灾打击\", cost = {runicPower = -10, runes = 1}},\n        [152280] = {name = \"亵渎\", cost = {runicPower = -10, runes = 1}},\n        [47541] = {name = \"凋零缠绕\", cost = {runicPower = 30, runes = 0}},\n        [207317] = {name = \"扩散\", cost = {runicPower = 30, runes = 0}},\n        [207289] = {name = \"邪恶突袭\", cost = {runicPower = 0, runes = 0}},\n        [275699] = {name = \"天启\", cost = {runicPower = 30, runes = 0}},\n        [343294] = {name = \"灵魂收割\", cost = {runicPower = -10, runes = 1}},\n        [390279] = {name = \"邪恶蔓延\", cost = {runicPower = 30, runes = 0}},\n        [455395] = {name = \"培育憎恶\", cost = {runicPower = 0, runes = 0}},\n    }\n\n    --技能缓存\n    local spellBookCache = {}\n    local function cachedFindSpellBookSlotBySpellID(spellID)\n        if spellBookCache[spellID] == nil then\n            spellBookCache[spellID] = FindSpellBookSlotBySpellID(spellID)\n        end\n        return spellBookCache[spellID]\n    end\n\n    -- 获取玩家状态\n    local function getPlayerState()\n        local state = {\n            runicPower = UnitPower(\"player\", Enum.PowerType.RunicPower),\n            runes = 0,\n            maxRunes = 6,  \n            hasPet = UnitExists(\"pet\"),\n            targetExists = UnitExists(\"target\"),\n            inCombat = UnitAffectingCombat(\"player\"),\n            buffs = {},\n            debuffs = {},\n            cooldowns = {},\n            targetCount = 0,\n        }\n    \n        for i = 1, 6 do\n            if select(3, GetRuneCooldown(i)) then\n                state.runes = state.runes + 1\n            end\n        end\n    \n        if state.targetExists then\n            local targetCurrentHealth = UnitHealth(\"target\")\n            local targetMaxHealth = UnitHealthMax(\"target\")\n            state.targetHealth = targetCurrentHealth\n            state.targetHealthPercentage = targetMaxHealth > 0 and (targetCurrentHealth / targetMaxHealth) * 100 or 0\n        else\n            state.targetHealth = 0\n            state.targetHealthPercentage = 0\n        end\n    \n        return state\n    end\n\n    -- 检查buff和CD\n    local function checkBuffsAndCDs(state)\n        local function checkAura(spellId, unit, auraType)\n            local name, _, count, _, duration, expirationTime, source, _, _, id = AuraUtil.FindAuraByName(C_Spell.GetSpellInfo(spellId).name, unit, auraType)\n            if id == spellId and (unit == \"player\" or source == \"player\") then\n                return {active = true, count = count or 0, remaining = expirationTime and (expirationTime - GetTime()) or 0}\n            end\n            return {active = false, count = 0, remaining = 0}\n        end\n        \n        local buffIds = {\n            81340,  -- 末日突降\n            188290, -- 枯萎凋零\n            390178, -- 瘟疫使者\n            458123, -- 浓疮毒镰\n        }\n        local debuffIds = {\n            191587, -- 恶性病变\n            194310, -- 溃烂之伤\n        }\n        \n        for _, id in ipairs(buffIds) do\n            state.buffs[id] = checkAura(id, \"player\", \"HELPFUL\")\n        end\n        \n        for _, id in ipairs(debuffIds) do\n            state.debuffs[id] = checkAura(id, \"target\", \"HARMFUL\")\n        end\n        \n        -- 检查技能CD\n        for spellId, spellInfo in pairs(spells) do\n            local spellCD = C_Spell.GetSpellCooldown(spellId)\n            state.cooldowns[spellId] = (spellCD.startTime + spellCD.duration) - GetTime()\n        end\n        \n        -- 统计受到恶性病变影响的目标数量\n        local function getAoeTargetCount()\n            local aoeTargets = 0\n            local maxTargetsToCheck = 3  -- 我们只需要知道是否有3个或更多目标\n        \n            for i = 1, 40 do\n                local unit = \"nameplate\" .. i\n                if UnitExists(unit) and UnitCanAttack(\"player\", unit) then\n                    local name, _, _, _, _, expirationTime, source, _, _, spellId = AuraUtil.FindAuraByName(C_Spell.GetSpellInfo(191587).name, unit, \"HARMFUL\")\n                    if spellId == 191587 and source == \"player\" then\n                        aoeTargets = aoeTargets + 1\n                        if aoeTargets >= maxTargetsToCheck then\n                            return maxTargetsToCheck  -- 如果已经找到3个目标，直接返回\n                        end\n                    end\n                end\n            end\n        \n            return aoeTargets\n        end\n        state.aoeTargets = getAoeTargetCount()\n    end\n\n    -- 获取技能冷却信息\n    local function getSpellCooldown(spellId)\n        local spellCD = C_Spell.GetSpellCooldown(spellId)\n        local spellCharges = C_Spell.GetSpellCharges(spellId)\n        if spellCharges then\n            local rechargeTime = (spellCharges.currentCharges < spellCharges.maxCharges) and (spellCharges.cooldownStartTime + spellCharges.cooldownDuration - GetTime()) or 0\n            return spellCharges.currentCharges, rechargeTime, spellCharges.maxCharges\n        elseif spellCD then\n            local remainingCD = (spellCD.startTime and spellCD.duration) and math.max(spellCD.startTime + spellCD.duration - GetTime(), 0) or 0\n            return 0, remainingCD, 0\n        else\n            return 0, 0, 0\n        end\n    end\n\n    -- 优先级判断函数\n    local function getPriority(state, excludeSpell)\n        local function canUseSpell(id)\n            if not cachedFindSpellBookSlotBySpellID(id) then\n                return false\n            end\n            if (state.cooldowns[id] and state.cooldowns[id] > 1.5) then\n                return false\n            end\n            if spells[id].cost.runicPower > 0 and state.runicPower < spells[id].cost.runicPower then\n                return false\n            end\n            if spells[id].cost.runes > 0 and state.runes < spells[id].cost.runes then\n                return false\n            end\n            return true\n        end\n        \n        local priorityList = {\n            {id = 46584, condition = function() -- 亡者复生\n                return not state.hasPet and excludeSpell ~= 46584\n            end},\n            {id = 85948, condition = function() -- 脓疮打击\n                return canUseSpell(275699)\n                    and state.debuffs[194310].count < 1\n                    and state.cooldowns[275699] < 1\n                    and state.runes > 1\n                    and excludeSpell ~= 85948\n            end},\n            {id = 275699, condition = function() -- 天启\n                return state.debuffs[194310].active and excludeSpell ~= 275699\n            end},\n            {id = 63560, condition = function() -- 黑暗突变\n                return ((canUseSpell(275699) and state.cooldowns[275699] > 0) or true) and excludeSpell ~= 63560\n            end},\n            {id = 207289, condition = function() -- 邪恶突袭\n                return not canUseSpell(455395) and excludeSpell ~= 207289\n            end},\n            {id = 77575, condition = function() -- 爆发\n                return state.debuffs[191587].remaining < 3 and excludeSpell ~= 77575\n            end},\n            {id = 85948, condition = function() -- 脓疮打击\n                return state.aoeTargets > 2 \n                    and state.debuffs[194310].count < 5\n                    and canUseSpell(390279)\n                    and state.runes > 1 \n                    and excludeSpell ~= 85948\n            end},\n            {id = 390279, condition = function() -- 邪恶蔓延\n                return state.aoeTargets > 2 \n                    and state.debuffs[194310].count > 4\n                    and excludeSpell ~= 390279\n            end},\n            {id = 85948, condition = function() -- 脓疮毒镰\n                return state.buffs[458123].active\n                    and (state.buffs[458123].remaining < 5\n                    or state.debuffs[194310].count < 4)\n                    and excludeSpell ~= 85948\n            end},\n            {id = 152280, condition = function() -- 亵渎\n                return state.aoeTargets > 2 and not state.buffs[188290].active and excludeSpell ~= 152280\n            end},\n            {id = 55090, condition = function() -- 天灾打击（蓄BUFF）\n                return state.aoeTargets > 2 \n                    and state.buffs[390178].remaining < 2 \n                    and state.runes > 0 \n                    and excludeSpell ~= 55090\n            end},\n            {id = 207317, condition = function() -- 扩散（带末日突降）\n                return state.buffs[81340].active and state.aoeTargets > 2 and state.runicPower >= 10\n            end},\n            {id = 47541, condition = function() -- 凋零缠绕（带末日突降或高能量）\n                return (state.buffs[81340].active and state.runicPower >= 10) or (state.runicPower >= 80 and state.aoeTargets < 3)\n            end},\n            {id = 343294, condition = function() -- 灵魂收割\n                return  state.cooldowns[343294] < 1 and state.targetExists and state.aoeTargets < 3 and state.targetHealthPercentage < 35 and excludeSpell ~= 343294\n            end},\n            {id = 207317, condition = function() -- 扩散（多目标高能量）\n                return state.aoeTargets > 2 and state.runicPower >= 80\n            end},\n            {id = 55090, condition = function() -- 天灾打击（多目标有枯萎有溃烂）\n                return state.buffs[188290].remaining > 1 and state.aoeTargets > 2 and state.debuffs[194310].active and state.runes > 0\n            end},\n            {id = 207317, condition = function() -- 扩散（多目标）\n                return state.aoeTargets > 2 and state.runicPower > 30\n            end},\n            {id = 207317, condition = function() -- 凋零缠绕(高能量)\n                return state.aoeTargets < 3 and state.runicPower >= 80\n            end},\n            {id = 55090, condition = function() -- 天灾打击（单目标有溃烂）\n                return state.aoeTargets < 3 and state.debuffs[194310].active and state.runes > 0\n            end},\n            {id = 47541, condition = function() -- 凋零缠绕\n                return state.runicPower > 30\n            end},\n            {id = 85948, condition = function() -- 脓疮打击\n                return true\n            end},\n        }\n\n        for _, spell in ipairs(priorityList) do\n            if canUseSpell(spell.id) and spell.condition() then\n                return spell.id\n            end\n        end\n\n        return 0\n    end\n\n    -- 主逻辑\n\n    local state = getPlayerState()\n    checkBuffsAndCDs(state)\n    -- print(state.runes)\n    local firstPriority = getPriority(state)\n    -- print(firstPriority)\n    local firstCharges, firstCD, firstMaxCharges = getSpellCooldown(firstPriority)\n\n    -- 更新状态\n    if spells[firstPriority] then\n        local spellInfo = spells[firstPriority]\n        local runicPowerCost = spellInfo.cost.runicPower\n        if state.buffs[81340].active and (firstPriority == 47541 or firstPriority == 207317) then\n            runicPowerCost = runicPowerCost - 20  -- 末日突降效果\n        end\n        state.runicPower = math.max(state.runicPower - runicPowerCost, 0)\n        state.runes = math.max(state.runes - spellInfo.cost.runes, 0)\n\n        if firstPriority == 46584 then  -- 亡者复生\n            state.hasPet = true\n        elseif firstPriority == 63560 or firstPriority == 77575 then  -- 疾病\n            state.debuffs[191587].active = true\n            state.debuffs[191587].remaining = 12 \n        elseif firstPriority == 85948 then  -- 脓疮打击\n            state.debuffs[194310].active = true\n            state.debuffs[194310].remaining = 28  \n        end\n\n        -- 减少所有 BUFF 和 DOT 的持续时间 1 秒\n        for _, buff in pairs(state.buffs) do\n            buff.remaining = math.max(buff.remaining - 1, 0)\n        end\n        for _, debuff in pairs(state.debuffs) do\n            debuff.remaining = math.max(debuff.remaining - 1, 0)\n            debuff.active = debuff.remaining > 0\n        end\n    end\n\n    local secondPriority = getPriority(state, firstPriority)\n    local secondCharges, secondCD, secondMaxCharges = getSpellCooldown(secondPriority)\n\n    -- 辅助函数：安全地获取法术图标\n    local function getSafeSpellIcon(spellId)\n        if not spellId or spellId == 0 then\n            return 0  \n        end\n        local spellInfo = C_Spell.GetSpellInfo(spellId)\n        return spellInfo and spellInfo.iconID or 0\n    end\n\n    -- 更新 allstates\n    allstates[1] = {\n        show = true,\n        changed = true,\n        icon = getSafeSpellIcon(firstPriority),\n        spell = firstPriority,\n        cooldown = firstCD,\n        charges = firstCharges,\n        maxCharges = firstMaxCharges\n    }\n\n    allstates[2] = {\n        show = secondPriority ~= 0,\n        changed = true,\n        icon = getSafeSpellIcon(secondPriority), \n        spell = secondPriority,\n        cooldown = secondCD,\n        charges = secondCharges,\n        maxCharges = secondMaxCharges\n    }\n\n    return true\nend",
