["event"] = "Health",
["unit"] = "player",
["custom"] = "function()\n    local MaxiLvl, EquippediLvl = GetAverageItemLevel()\n    local BothiLvl = aura_env.config.BothiLvls\n    local DecimalsToFormat = aura_env.config.DecimalsToFormat\n    local lowiLvl = aura_env.config.lowiLvl\n    local maxiLvl = aura_env.config.maxiLvl\n    local mediLvl = (lowiLvl + maxiLvl) / 2\n    local highiLvl = (mediLvl + maxiLvl) / 2\n    local iLvlColour = \"FF40FF40\"\n    local colorByiLvl = aura_env.config.colorByiLvl\n    \n    if EquippediLvl > lowiLvl and EquippediLvl < mediLvl then\n        iLvlColour = \"FF1EFF00\"\n    elseif EquippediLvl > mediLvl and EquippediLvl < highiLvl then\n        iLvlColour = \"FF006EFF\"\n    elseif EquippediLvl > highiLvl and EquippediLvl < maxiLvl then\n        iLvlColour = \"FFA435EE\"\n    elseif EquippediLvl > maxiLvl then\n        iLvlColour = \"FFFF8000\"\n    end\n    \n    if BothiLvl and colorByiLvl then\n        aura_env.iLvl = string.format(\"|c%s%.\" .. DecimalsToFormat .. \"f / %.\" .. DecimalsToFormat .. \"f|r\", iLvlColour, EquippediLvl, MaxiLvl)\n    elseif not BothiLvl and colorByiLvl then\n        aura_env.iLvl = string.format(\"|c%s%.\" .. DecimalsToFormat .. \"f|r\", iLvlColour, EquippediLvl)\n    elseif BothiLvl and not colorByiLvl then\n        aura_env.iLvl = string.format(\"%.\" .. DecimalsToFormat .. \"f / %.\" .. DecimalsToFormat .. \"f\", EquippediLvl, MaxiLvl)\n    elseif not BothiLvl and not colorByiLvl then\n        aura_env.iLvl = string.format(\"%.\" .. DecimalsToFormat .. \"f\", EquippediLvl)\n    end\n    \n    \n    \n    return true\nend",
