["custom"] = "function(allstates)\n    -- build vars\n    local cfg  = aura_env.config.weekly\n    local lang = GetLocale()\n    local dow  = C_DateAndTime.GetCurrentCalendarTime().weekday\n    \n    -- is this thing even on?\n    if not cfg.weeklyEnable then return end\n    \n    -- start checking weekly events\n    for event, v in pairs(aura_env.db) do\n        local name = v.name[lang] or v.name[\"enUS\"]\n        local questDB = v.questID\n        local required = v.questReq\n        local info = \"\"\n        local tooltip = \"Incomplete Events: |n|n|cFFFFFFFF\"\n        \n        -- should we care about this event?\n        if cfg[event] then\n            \n            -- start checking quest progress\n            local completed = 0\n            for _, questID in pairs(questDB) do\n                -- done?\n                local questComplete = C_QuestLog.IsQuestFlaggedCompleted(questID)\n                if questComplete then completed = completed + 1 end\n                \n                -- info: adventurer's cache progress\n                if event == \"adventureCache\" then\n                    local c = questComplete and \"|cFFaad371\" or \"\"\n                    info = c .. completed .. \"/\" .. required .. \"|r\"\n                end\n                \n                -- itorate through event information (mainly for world boss tracking)\n                if v.questInfo then\n                    for infoID, x in pairs(v.questInfo) do\n                        if questID == infoID and event == \"worldBoss\" and (C_TaskQuest.IsActive(questID) or questComplete) then\n                            info = (questComplete and \"|cFFaad371\" or \"\") .. (x.name[lang] or x.name[\"enUS\"]) .. \"|r\"\n                        end\n                    end\n                end\n                \n            end\n            local IsComplete = completed >= required and true or false\n            \n            -- update the bar?\n            if allstates[event] then\n                if cfg.hideComplete and IsComplete then\n                    allstates[event] = { changed = true, show = false }\n                elseif not cfg.hideComplete and IsComplete then\n                    allstates[event] = { changed = true, info = info }\n                end\n            end\n            \n            -- build the bars\n            if v.isWeekend and cfg.weekend and (dow > 1 and dow < 7) then -- hide weekend events during the week (if enabled)\n            elseif IsComplete and not cfg.hideComplete or not IsComplete then\n                allstates[event] = {\n                    autoHide     = true,\n                    changed      = true,\n                    event        = event,\n                    index        = name,\n                    info         = info,\n                    IsComplete   = IsComplete,\n                    name         = name,\n                    progressType = \"static\",\n                    show         = true,\n                    tooltip      = tooltip or \"\",\n                    total        = 100,\n                    value        = 0,\n                }\n            end\n        end  \n    end\n    \n    return true   \nend",
