["events"] = "THE_WAR_WITHIN_EVENT_TRACKER",
["custom"] = "function(allstates, evt, id)\n    \n    -- set ourselves up for success\n    local cfg  = aura_env.config.activities\n    local lang = GetLocale()\n    \n    -- check all our events from database\n    for event, data in pairs(aura_env.db) do\n        \n        -- check completion\n        local questP, questT = 0, data.questReq\n        for _, questID in pairs(data.questID) do\n            if questID ~= 0 then\n                questP = C_QuestLog.IsQuestFlaggedCompleted(questID) and questP + 1 or questP\n            end\n        end\n        local IsComplete = questP == questT and true or false\n        \n        -- is this thing on?\n        if cfg.activityEnable and cfg[event] and ((cfg.hideComplete and not IsComplete) or (not cfg.hideComplete) or (evt == \"OPTIONS\")) then\n            -- it's basic geometry (build offsets, etc)\n            local region    = cfg.regionForce.enable and cfg.regionForce.region or GetCurrentRegion()\n            local first     = data.epoch[region]\n            local interval  = data.interval\n            local duration  = data.duration\n            local offset    = cfg.timerOffset[event] * 60\n            local next      = interval - (((GetServerTime() + offset) - first) % interval)\n            local active    = interval - next < duration\n            local remaining = duration - (interval - next)\n            \n            -- build bar\n            allstates[event] = {\n                active         = active,\n                alert          = cfg.eventAlerts[data.alert] and true or false,\n                autoHide       = true,\n                changed        = true,\n                color          = data.color,\n                duration       = active and duration or interval - duration,\n                event          = event,\n                expirationTime = GetTime() + (active and remaining or next),\n                index          = data.name[lang] or data.name[\"enUS\"],\n                name           = data.name[lang] or data.name[\"enUS\"],\n                progressType   = \"timed\",\n                remaining      = next,\n                show           = true,\n            }\n        end\n    end\n    \n    return true\nend",
